mapper.select:
select ccolorcode, ssize, samount
from stock
where pid = #{pid}
  and ccolorcode = #{ccolorcode}


select pid, bname, pname, pprice, ccolorcode, cimage1, cimage2
from (
         select rownum rr,
                p.pid,
                p.bname,
                p.pname,
                p.pprice,
                c.ccolorcode,
                c.cimage1,
                c.cimage2
         from product p
                  join v_color1 c on p.pid = c.pid
         where clarge = #{param2}
           and cmedium = #{param3}
           and csmall = #{param4}
     )
where rr > (#{param1.pageNum} - 1) * #{param1.amount}
  and rr <= (#{param1.pageNum} * #{param1.amount})

		 select count(*) from product p
         join v_color1 c on p.pid = c.pid
 			where clarge = #{param1}
            and cmedium = #{param2}
	        and csmall = #{param3}

mapper.update:
update product set pid=#{pid}, clage=#{clarge}, cmedium=#{cmedium},
csmall=#{csmall}, bname=#{bname}, pname=#{pname}, pprice=#{pprice}, pdatail=#{pdetail}
where pid = #{pid}
